<script lang="ts">
  import { Chessground } from "chessground"
  import type { Drop, FEN, Key, MouchEvent, Piece, PiecesDiff } from "chessground/types"

  import type { Api } from "chessground/api"
  import type { Config } from "chessground/config"
  import type { DrawBrush, DrawBrushes, DrawShape } from "chessground/draw"
  import type { State } from "chessground/state"
  import { onMount } from "svelte"

  let {
    /**
     * CSS class name applied to wrapping div. Set this to apply another
     * stylesheet than the default.
     */
    class: className = "cg-default-style",
    brushes = undefined as { [color: string]: DrawBrush } | undefined,
    ...restProps
  } = $props()

  let container: HTMLDivElement
  let chessground: Api

  /**
   * Set config values. Alternative to using the config prop.
   * @param {import('chessground/config').Config} config - object of key(s) and value(s) to set.
   * @returns {void}
   */
  export function set(config: Config): void {
    chessground.set(config)
  }

  /**
   * Internal Chessground state, described at
   * https://github.com/lichess-org/chessground/blob/master/src/state.ts
   * @returns {import('chessground/state').State} - Chessground state
   */
  export function getState(): State {
    return chessground.state
  }

  /**
   * Current position as a FEN string. Contains only pieces, no flags.
   * e.g. "rnbqkbnr/pppppppp/8/8/8/8/PPPPPPPP/RNBQKBNR"
   * @returns {import('chessground/types').FEN} - FEN string
   */
  export function getFen(): FEN {
    return chessground.getFen()
  }

  /**
   * Toggle chessboard orientation between white and black. Can also be
   * handled via the orientation prop.
   * @returns {void}
   */
  export function toggleOrientation(): void {
    return chessground.toggleOrientation()
  }

  /**
   * Perform a move programmatically.
   * @param {import('chessground/types').Key} orig - Origin square.
   * @param {import('chessground/types').Key} dest - Destination square.
   * @returns {void}
   */
  export function move(orig: Key, dest: Key): void {
    return chessground.move(orig, dest)
  }

  /**
   * Add and/or remove arbitrary pieces on the board.
   * @param {import('chessground/types').PiecesDiff} pieces - Pieces to add/remove.
   * @returns {void}
   */
  export function setPieces(pieces: PiecesDiff): void {
    return chessground.setPieces(pieces)
  }

  /**
   * Click a square programmatically.
   * @param {import('chessground/types').Key | null } key - Square to click.
   * @param {boolean} [force] - Click even if the squares are not selectable (config.selectable).
   * @returns {void}
   */
  export function selectSquare(key: Key | null, force: boolean): void {
    return chessground.selectSquare(key, force)
  }

  /**
   * Put a new piece on the board.
   * @param {import('chessground/types').Piece } piece - Piece to place.
   * @param {import('chessground/types').Key } key - Square to place the piece.
   * @returns {void}
   */
  export function newPiece(piece: Piece, key: Key): void {
    return chessground.newPiece(piece, key)
  }

  /**
   * Play the current premove, if any.
   * @returns {boolean} - True if a premove was played.
   */
  export function playPremove(): boolean {
    return chessground.playPremove()
  }

  /**
   * Cancel the current premove, if any.
   * @returns {void}
   */
  export function cancelPremove(): void {
    chessground.cancelPremove()
  }

  /**
   * Play the current predrop, if any.
   * @param {(drop: import('chessground/types').Drop) => boolean } validate - Predicate to decide whether a drop is valid.
   * @returns {boolean} - True if a predrop was played.
   */
  export function playPredrop(validate: (drop: Drop) => boolean): boolean {
    return chessground.playPredrop(validate)
  }

  /**
   * Cancel the current predrop, if any.
   * @returns {void}
   */
  export function cancelPredrop(): void {
    chessground.cancelPredrop()
  }

  /**
   * Cancel the current move being made.
   * @returns {void}
   */
  export function cancelMove(): void {
    chessground.cancelMove()
  }

  /**
   * Cancel the current move and prevent further ones.
   * @returns {void}
   */
  export function stop(): void {
    chessground.stop()
  }

  /**
   * Make squares explode (for atomic chess).
   * @param {import('chessground/types').Key[] } keys - Squares to explode.
   * @returns {void}
   */
  export function explode(keys: Key[]): void {
    chessground.explode(keys)
  }

  /**
   * Programmatically draw user shapes.
   * @param {import('chessground/draw').DrawShape[] } shapes - Shapes to draw.
   * @returns {void}
   */
  export function setShapes(shapes: DrawShape[]): void {
    chessground.setShapes(shapes)
  }

  /**
   * Programmatically draw auto shapes.
   * @param {import('chessground/draw').DrawShape[] } shapes - Shapes to draw.
   * @returns {void}
   */
  export function setAutoShapes(shapes: DrawShape[]): void {
    chessground.setAutoShapes(shapes)
  }

  /**
   * Redraw. Useful when CSS changes the board width or height.
   * @returns {void}
   */
  export function redrawAll(): void {
    return chessground.redrawAll()
  }

  /**
   * Drag new piece, for crazyhouse and board editors.
   * @param {import('chessground/types').Piece } piece - Piece to drop
   * @param {import('chessground/types').MouchEvent } event - The mouse/touch event identifying a target.
   * @param {boolean} [force] - True if the new piece can replace an existing one.
   * @returns {void}
   */
  export function dragNewPiece(piece: Piece, event: MouchEvent, force: boolean): void {
    chessground.dragNewPiece(piece, event, force)
  }

  /** @returns {void} */
  export function destroy(): void {
    return chessground.destroy()
  }

  onMount(() => {
    const config: Config = {
      drawable: {
        enabled: false
      }
    }
    if (brushes) config.drawable!.brushes = brushes as DrawBrushes
    chessground = Chessground(container, config)
    return chessground.destroy
  })
</script>

<div {...restProps} class="cg-wrap {className}" bind:this={container}></div>

<style>
  .cg-wrap {
    width: 100%;
    aspect-ratio: 1;
  }

  @supports not (aspect-ratio: 1) {
    .cg-wrap {
      padding-top: 100%;
    }
  }

  /*
	 * Board styling.
	 *
	 * As there is no bundler-agnostic way of including CSS files (please
	 * correct me if I'm wrong), the default Chessground styles are
	 * included here.
	 */

  /*
	 * chessground.base.css
	 */

  .cg-default-style.cg-wrap {
    box-sizing: content-box;
    position: relative;
    display: block;
  }

  .cg-default-style :global(cg-container) {
    position: absolute;
    width: 100%;
    height: 100%;
    display: block;
    top: 0;
  }

  .cg-default-style :global(cg-board) {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    line-height: 0;
    background-size: cover;
  }

  .cg-default-style.cg-wrap.manipulable :global(cg-board) {
    cursor: pointer;
  }

  .cg-default-style :global(cg-board square) {
    position: absolute;
    top: 0;
    left: 0;
    width: 12.5%;
    height: 12.5%;
    pointer-events: none;
  }

  .cg-default-style :global(cg-board square.move-dest) {
    pointer-events: auto;
  }

  .cg-default-style :global(cg-board square.last-move) {
    will-change: transform;
  }

  .cg-default-style.cg-wrap :global(piece) {
    position: absolute;
    top: 0;
    left: 0;
    width: 12.5%;
    height: 12.5%;
    background-size: cover;
    z-index: 2;
    will-change: transform;
    pointer-events: none;
  }

  .cg-default-style :global(cg-board piece.dragging) {
    cursor: move;
    /* !important to override z-index from 3D piece inline style */
    z-index: 11 !important;
  }

  .cg-default-style :global(piece.anim) {
    z-index: 8;
  }

  .cg-default-style :global(piece.fading) {
    z-index: 1;
    opacity: 0.5;
  }

  .cg-default-style.cg-wrap :global(piece.ghost) {
    opacity: 0.3;
  }

  .cg-default-style.cg-wrap :global(piece svg) {
    overflow: hidden;
    position: relative;
    top: 0px;
    left: 0px;
    width: 100%;
    height: 100%;
    pointer-events: none;
    z-index: 2;
    opacity: 0.6;
  }

  .cg-default-style.cg-wrap :global(cg-auto-pieces),
  .cg-default-style.cg-wrap :global(.cg-shapes),
  .cg-default-style.cg-wrap :global(.cg-custom-svgs) {
    overflow: visible;
    position: absolute;
    top: 0px;
    left: 0px;
    width: 100%;
    height: 100%;
    pointer-events: none;
  }

  .cg-default-style.cg-wrap :global(cg-auto-pieces) {
    z-index: 2;
  }

  .cg-default-style.cg-wrap :global(cg-auto-pieces piece) {
    opacity: 0.3;
  }

  .cg-default-style.cg-wrap :global(.cg-shapes) {
    overflow: hidden;
    opacity: 0.6;
    z-index: 2;
  }

  .cg-default-style.cg-wrap :global(.cg-custom-svgs) {
    /* over piece.anim = 8, but under piece.dragging = 11 */
    z-index: 9;
  }

  .cg-default-style.cg-wrap :global(.cg-custom-svgs svg) {
    overflow: visible;
  }

  .cg-default-style.cg-wrap :global(coords) {
    position: absolute;
    display: flex;
    pointer-events: none;
    opacity: 0.8;
    font-family: sans-serif;
    font-size: 9px;
  }

  .cg-default-style.cg-wrap :global(coords.ranks) {
    left: 4px;
    top: -20px;
    flex-flow: column-reverse;
    height: 100%;
    width: 12px;
  }

  .cg-default-style.cg-wrap :global(coords.ranks.black) {
    flex-flow: column;
  }

  .cg-default-style.cg-wrap :global(coords.ranks.left) {
    left: -15px;
    align-items: flex-end;
  }

  .cg-default-style.cg-wrap :global(coords.files) {
    bottom: -4px;
    left: 24px;
    flex-flow: row;
    width: 100%;
    height: 16px;
    text-transform: uppercase;
    text-align: center;
  }

  .cg-default-style.cg-wrap :global(coords.files.black) {
    flex-flow: row-reverse;
  }

  .cg-default-style.cg-wrap :global(coords coord) {
    flex: 1 1 auto;
  }

  .cg-default-style.cg-wrap :global(coords.ranks coord) {
    transform: translateY(39%);
  }

  /*
	 * chessground.cbrown.css - board appearence
	 */

  /** Colored board squares as an embedded SVG */
  .cg-default-style :global(cg-board) {
    background-color: #f0d9b5;
    background-image: url("data:image/svg+xml;base64,");
  }

  /** Interactive board square colors */
  .cg-default-style :global(square.move-dest) {
    background: radial-gradient(rgba(20, 85, 30, 0.5) 22%, #208530 0, rgba(0, 0, 0, 0.3) 0, rgba(0, 0, 0, 0) 0);
  }
  .cg-default-style :global(square.premove-dest) {
    background: radial-gradient(rgba(20, 30, 85, 0.5) 22%, #203085 0, rgba(0, 0, 0, 0.3) 0, rgba(0, 0, 0, 0) 0);
  }
  .cg-default-style :global(square.oc.move-dest) {
    background: radial-gradient(transparent 0%, transparent 80%, rgba(20, 85, 0, 0.3) 80%);
  }
  .cg-default-style :global(square.oc.premove-dest) {
    background: radial-gradient(transparent 0%, transparent 80%, rgba(20, 30, 85, 0.2) 80%);
  }
  .cg-default-style :global(square.move-dest:hover) {
    background: rgba(20, 85, 30, 0.3);
  }
  .cg-default-style :global(square.premove-dest:hover) {
    background: rgba(20, 30, 85, 0.2);
  }
  .cg-default-style :global(square.last-move) {
    background-color: rgba(155, 199, 0, 0.41);
  }
  .cg-default-style :global(square.selected) {
    background-color: rgba(20, 85, 30, 0.5);
  }
  .cg-default-style :global(square.check) {
    background: radial-gradient(
      ellipse at center,
      rgba(255, 0, 0, 1) 0%,
      rgba(231, 0, 0, 1) 25%,
      rgba(169, 0, 0, 0) 89%,
      rgba(158, 0, 0, 0) 100%
    );
  }
  .cg-default-style :global(square.current-premove) {
    background-color: rgba(20, 30, 85, 0.5);
  }

  /** Alternating colors in rank/file labels */
  .cg-default-style.cg-wrap.orientation-white :global(coords.ranks coord:nth-child(2n)),
  .cg-default-style.cg-wrap.orientation-white :global(coords.files coord:nth-child(2n)),
  .cg-default-style.cg-wrap.orientation-black :global(coords.ranks coord:nth-child(2n + 1)),
  .cg-default-style.cg-wrap.orientation-black :global(coords.files coord:nth-child(2n + 1)) {
    color: rgba(72, 72, 72, 0.8);
  }

  .cg-default-style.cg-wrap.orientation-black :global(coords.ranks coord:nth-child(2n)),
  .cg-default-style.cg-wrap.orientation-black :global(coords.files coord:nth-child(2n)),
  .cg-default-style.cg-wrap.orientation-white :global(coords.ranks coord:nth-child(2n + 1)),
  .cg-default-style.cg-wrap.orientation-white :global(coords.files coord:nth-child(2n + 1)) {
    color: rgba(255, 255, 255, 0.8);
  }

  /*
	 * chessground.cburnett.css - embedded SVGs for all chess pieces
	 */

  .cg-default-style.cg-wrap :global(piece.pawn.white) {
    background-image: url("data:image/svg+xml;base64,");
  }
  .cg-default-style.cg-wrap :global(piece.bishop.white) {
    background-image: url("data:image/svg+xml;base64,");
  }
  .cg-default-style.cg-wrap :global(piece.knight.white) {
    background-image: url("data:image/svg+xml;base64,");
  }
  .cg-default-style.cg-wrap :global(piece.rook.white) {
    background-image: url("data:image/svg+xml;base64,");
  }
  .cg-default-style.cg-wrap :global(piece.queen.white) {
    background-image: url("data:image/svg+xml;base64,");
  }
  .cg-default-style.cg-wrap :global(piece.king.white) {
    background-image: url("data:image/svg+xml;base64,");
  }
  .cg-default-style.cg-wrap :global(piece.pawn.black) {
    background-image: url("data:image/svg+xml;base64,");
  }
  .cg-default-style.cg-wrap :global(piece.bishop.black) {
    background-image: url("data:image/svg+xml;base64,");
  }
