<script lang="ts">
  import type { Color } from "chessops"

  interface Props {
    square: any
    color?: Color
    callback: any
    class?: any
  }

  let { square, color = "white", callback, class: className = void 0 }: Props = $props()

  const marginLeft = (100 / 8) * (color === "white" ? squareToFileNumber(square) : 7 - squareToFileNumber(square))
  const white = square.charAt(1) === "8"
  const black = !white
  const pieces = ["queen", "knight", "rook", "bishop"]
  const pieceNames = { q: "queen", n: "knight", r: "rook", b: "bishop", p: "pawn", k: "king" }
  function squareToFileNumber(square2: string) {
    return square2.charCodeAt(0) - 97
  }
  function keyboardCallback(event: KeyboardEvent & { currentTarget: EventTarget & HTMLDivElement }, promotion: string) {
    if (event.key === "Enter" || event.key === " ") {
      callback(promotion)
    }
  }
</script>

<div class="dialog {className}">
  {#each pieces as piece, i}
    {@const putPiecesFromTop = (white && color === "white") || (black && color === "black")}
    {@const marginTop = putPiecesFromTop ? i * 12.5 : 100 - 12.5 * (i + 1)}
    <div class="square" style="margin-left:{marginLeft}%;margin-top:{marginTop}%;">
      <div
        class="piece {piece}"
        class:white
        class:black
        onclick={() => callback(piece)}
        onkeydown={(e) => keyboardCallback(e, piece)}
        role="button"
        tabindex="0"
        aria-label="Promote to {pieceNames[piece]}"
      ></div>
    </div>
  {/each}
</div>

<style>
  div.dialog {
    width: 100%;
    height: 100%;
    position: absolute;
    top: 0;
    left: 0;
    z-index: 10;
    background: rgba(255, 255, 255, 0.3);
  }
  div.square {
    position: absolute;
    width: 12.5%;
    height: 12.5%;
    background-color: #b0b0b0;
    border-radius: 50%;
    box-shadow: inset 0 0 25px 3px grey;
    transition: all 150ms;
  }
  div.square:hover {
    border-radius: 0;
  }
  div.square div.piece {
    width: 100%;
    height: 100%;
    background-size: cover;
    cursor: pointer;
    transition: all 150ms;
    transform: scale(0.8);
  }
  div.square:hover div.piece {
    transform: scale(1);
  }

  /* Chessground default pieces */
  .bishop.white {
    background-image: url("data:image/svg+xml;base64,");
  }
  .knight.white {
    background-image: url("data:image/svg+xml;base64,");
  }
  .rook.white {
    background-image: url("data:image/svg+xml;base64,");
  }
  .queen.white {
    background-image: url("data:image/svg+xml;base64,");
  }
  .bishop.black {
    background-image: url("data:image/svg+xml;base64,");
  }
  .knight.black {
    background-image: url("data:image/svg+xml;base64,");
  }
  .rook.black {
    background-image: url("data:image/svg+xml;base64,");
  }
  .queen.black {
    background-image: url("data:image/svg+xml;base64,");
  }
</style>
